/* =========================================================
   THEME TOGGLE + MARK MODE SETTINGS PANEL
   ========================================================= */
var MARK_PRESETS = {
  darkroom:     { brightness:85,  contrast:110, saturation:90,  fontScale:100 },
  office:       { brightness:100, contrast:100, saturation:100, fontScale:100 },
  presentation: { brightness:115, contrast:120, saturation:110, fontScale:115 },
  outdoor:      { brightness:130, contrast:130, saturation:80,  fontScale:110 },
  custom:       null
};
var markSettings = { preset:'office', brightness:100, contrast:100, saturation:100, fontScale:100 };

function applyMarkSettings(s) {
  var m = document.querySelector('.main');
  if (!m) return;
  m.style.setProperty('--mm-bright', (s.brightness / 100));
  m.style.setProperty('--mm-contrast', (s.contrast / 100));
  m.style.setProperty('--mm-sat', (s.saturation / 100));
  m.style.setProperty('--mm-font-scale', (s.fontScale / 100));
}

function clearMarkFilters() {
  var m = document.querySelector('.main');
  if (!m) return;
  m.style.removeProperty('--mm-bright');
  m.style.removeProperty('--mm-contrast');
  m.style.removeProperty('--mm-sat');
  m.style.removeProperty('--mm-font-scale');
}

function updateMarkUI() {
  document.getElementById('mm-bright').value = markSettings.brightness;
  document.getElementById('mm-contrast').value = markSettings.contrast;
  document.getElementById('mm-sat').value = markSettings.saturation;
  document.getElementById('mm-font').value = markSettings.fontScale;
  document.getElementById('mm-bright-val').textContent = markSettings.brightness + '%';
  document.getElementById('mm-contrast-val').textContent = markSettings.contrast + '%';
  document.getElementById('mm-sat-val').textContent = markSettings.saturation + '%';
  document.getElementById('mm-font-val').textContent = markSettings.fontScale + '%';
  document.querySelectorAll('.mark-preset').forEach(function(p) {
    p.classList.toggle('active', p.getAttribute('data-preset') === markSettings.preset);
  });
}

function selectPreset(name) {
  var vals = MARK_PRESETS[name];
  if (vals) {
    markSettings.brightness = vals.brightness;
    markSettings.contrast = vals.contrast;
    markSettings.saturation = vals.saturation;
    markSettings.fontScale = vals.fontScale;
  }
  markSettings.preset = name;
  updateMarkUI();
  applyMarkSettings(markSettings);
  saveMarkSettings();
}

function onMarkSlider() {
  markSettings.brightness = parseInt(document.getElementById('mm-bright').value);
  markSettings.contrast = parseInt(document.getElementById('mm-contrast').value);
  markSettings.saturation = parseInt(document.getElementById('mm-sat').value);
  markSettings.fontScale = parseInt(document.getElementById('mm-font').value);
  document.getElementById('mm-bright-val').textContent = markSettings.brightness + '%';
  document.getElementById('mm-contrast-val').textContent = markSettings.contrast + '%';
  document.getElementById('mm-sat-val').textContent = markSettings.saturation + '%';
  document.getElementById('mm-font-val').textContent = markSettings.fontScale + '%';
  var matched = false;
  Object.keys(MARK_PRESETS).forEach(function(k) {
    var p = MARK_PRESETS[k];
    if (p && p.brightness === markSettings.brightness && p.contrast === markSettings.contrast && p.saturation === markSettings.saturation && p.fontScale === markSettings.fontScale) {
      markSettings.preset = k; matched = true;
    }
  });
  if (!matched) markSettings.preset = 'custom';
  document.querySelectorAll('.mark-preset').forEach(function(el) {
    el.classList.toggle('active', el.getAttribute('data-preset') === markSettings.preset);
  });
  applyMarkSettings(markSettings);
  saveMarkSettings();
}

function saveMarkSettings() {
  localStorage.setItem('cd-mark-settings', JSON.stringify(markSettings));
}

function loadMarkSettings() {
  try {
    var stored = JSON.parse(localStorage.getItem('cd-mark-settings'));
    if (stored && typeof stored.brightness === 'number') {
      markSettings = stored;
    }
  } catch(e) {}
}

function openMarkPanel() {
  loadMarkSettings();
  updateMarkUI();
  applyMarkSettings(markSettings);
  document.getElementById('mark-overlay').classList.add('active');
}

function closeMarkPanel() {
  document.getElementById('mark-overlay').classList.remove('active');
}

function toggleTheme() {
  var isMark = document.body.classList.toggle('mark-mode');
  document.getElementById('theme-label').textContent = isMark ? 'Dark Mode' : 'Mark Mode';
  localStorage.setItem('cd-theme', isMark ? 'mark' : 'dark');
  if (isMark) {
    openMarkPanel();
  } else {
    closeMarkPanel();
    clearMarkFilters();
  }
}

(function initThemeAndMark(){
  var isMark = document.body.classList.contains('mark-mode');
  document.getElementById('theme-label').textContent = isMark ? 'Dark Mode' : 'Mark Mode';
  if (isMark) {
    loadMarkSettings();
    applyMarkSettings(markSettings);
  }
  document.addEventListener('keydown', function(e) {
    if (e.key === 'Escape') closeMarkPanel();
  });
})();

