function toggleSection(el) { el.classList.toggle('collapsed'); }

function showView(id) {
  document.querySelectorAll('.view').forEach(function(v){ v.classList.remove('active'); });
  document.querySelectorAll('.nav-item').forEach(function(n){ n.classList.remove('active'); });
  document.getElementById('view-'+id).classList.add('active');
  var navItems = document.querySelectorAll('.nav-item');
  var viewMap = ['commandcenter','overview','wireless','apcompare','switching','sdwan','management','fabric','costvalue','tcocalc','licensing','certs','compliance','rfpkill','regrisk','supplyrisk','psirt','momentum','support','sustainability','feedback','opsfriction','aimaturity','techdata','playbook','threats','eoseol','battlecard','dealstrategy','lowfruit','erate470'];
  var idx = viewMap.indexOf(id);
  if(idx >= 0) {
    navItems[idx].classList.add('active');
    var grp = navItems[idx].parentElement;
    if(grp && grp.classList.contains('nav-group')) {
      var sec = grp.previousElementSibling;
      if(sec && sec.classList.contains('collapsed')) sec.classList.remove('collapsed');
    }
  }
}

function switchTab(view, tab) {
  var parent = document.getElementById('view-'+view);
  parent.querySelectorAll('.tab-panel').forEach(function(p){ p.classList.remove('active'); });
  parent.querySelectorAll('.tab-btn').forEach(function(b){ b.classList.remove('active'); });
  document.getElementById(view+'-'+tab).classList.add('active');
  var tabs = parent.querySelectorAll('.tab-btn');
  var tabMaps = {
    costvalue:['tco','value','licensing','hidden'],
    techdata:['catalog','replacements'],
    certs:['cloud','product','why'],
    apcompare:['portfolio','catalog','h2h'],
    erate470:['cover','products','datasheets','advantages','tco','rfpresponse'],
    _default:['features','pricing','market']
  };
  var tabMap = tabMaps[view] || tabMaps._default;
  var idx = tabMap.indexOf(tab);
  if(idx >= 0) tabs[idx].classList.add('active');
}

