function filterTDReplacements() {
  var venSel = document.getElementById('td-rep-vendor');
  var catSel = document.getElementById('td-rep-cat');
  var ven = venSel ? venSel.value : 'all';
  var cat = catSel ? catSel.value : 'all';
  var filtered = REPLACEMENTS.filter(function(r) {
    if(ven !== 'all' && r.source !== ven) return false;
    if(cat !== 'all' && r.sourceCategory !== cat) return false;
    return true;
  });

  // Group by source product
  var groups = {};
  filtered.forEach(function(r) {
    var key = r.source + '|' + r.sourceModel;
    if(!groups[key]) groups[key] = { source:r.source, sourceModel:r.sourceModel, sourceCategory:r.sourceCategory, replacements:[] };
    groups[key].replacements.push(r);
  });

  var html = '';
  var groupKeys = Object.keys(groups);
  groupKeys.forEach(function(key) {
    var g = groups[key];
    var sv = vendorByCode(g.source);
    html += '<div style="margin-bottom:24px">';
    html += '<div style="display:flex;align-items:center;gap:8px;margin-bottom:10px">';
    html += '<span class="td-dot" style="background:'+sv.color+';width:10px;height:10px;border-radius:50%;display:inline-block"></span>';
    html += '<span style="font-size:15px;font-weight:700;color:'+sv.color+'">'+sv.name+' '+g.sourceModel+'</span>';
    html += '<span style="font-size:12px;color:var(--text-dim)">'+g.sourceCategory+'</span>';
    html += '</div>';

    // Sort: Extreme first, then by match quality
    var matchOrder = {excellent:0, good:1, fair:2};
    g.replacements.sort(function(a,b) {
      if(a.target === 'EXTR' && b.target !== 'EXTR') return -1;
      if(a.target !== 'EXTR' && b.target === 'EXTR') return 1;
      return (matchOrder[a.match]||3) - (matchOrder[b.match]||3);
    });

    g.replacements.forEach(function(r) {
      var tv = vendorByCode(r.target);
      var isExtr = r.target === 'EXTR';
      html += '<div class="td-replace-card" style="'+(isExtr?'border-color:rgba(123,47,247,.4);background:rgba(123,47,247,.03)':'')+'">';
      html += '<div class="td-rh">';
      html += '<span class="td-source" style="color:'+sv.color+'">'+g.sourceModel+'</span>';
      html += '<span class="td-arrow">â†’</span>';
      html += '<span class="td-target" style="color:'+tv.color+'">'+tv.name+' '+r.targetModel+'</span>';
      if(isExtr) html += '<span style="font-size:10px;color:var(--accent-light);font-weight:600;margin-left:8px">RECOMMENDED</span>';
      html += '<span class="td-match '+r.match+'">'+r.match.charAt(0).toUpperCase()+r.match.slice(1)+' Match</span>';
      html += '</div>';
      html += '<div class="td-rb">';
      html += '<div class="td-reason">'+r.reason+'</div>';
      if(r.advantages && r.advantages.length) {
        html += '<div class="td-adv" style="font-weight:600;font-size:11px;margin-bottom:2px">Advantages</div>';
        html += '<ul class="adv-list">';
        r.advantages.forEach(function(a){ html += '<li>'+a+'</li>'; });
        html += '</ul>';
      }
      if(r.tradeoffs && r.tradeoffs.length) {
        html += '<div class="td-trd" style="font-weight:600;font-size:11px;margin-top:8px;margin-bottom:2px">Trade-offs to Consider</div>';
        html += '<ul class="trd-list">';
        r.tradeoffs.forEach(function(t){ html += '<li>'+t+'</li>'; });
        html += '</ul>';
      }
      html += '</div></div>';
    });
    html += '</div>';
  });

  if(groupKeys.length === 0) {
    html = '<div style="text-align:center;padding:40px;color:var(--text-dim)">No replacement recommendations found for the selected filters.</div>';
  }

  document.getElementById('td-rep-grid').innerHTML = html;
  document.getElementById('td-rep-count').textContent = filtered.length + ' replacement recommendation' + (filtered.length !== 1 ? 's' : '') + ' across ' + groupKeys.length + ' product' + (groupKeys.length !== 1 ? 's' : '');
}

