function renderPlaybookH2H() {
  var codes = ['EXTR','CSCO','MRAK','ARBA','JNPR','FTNT','RKUS','UBNT','ARST','ALE'];
  var colBdr = 'border-left:1px dotted rgba(136,136,136,.35)';
  var shortNames = {EXTR:'EXTREME',CSCO:'CISCO',MRAK:'MERAKI',ARBA:'ARUBA',JNPR:'JUNIPER',FTNT:'FORTINET',RKUS:'RUCKUS',UBNT:'UBIQUITI',ARST:'ARISTA',ALE:'ALC-LUC'};

  var stats = {};
  var scores = {};
  codes.forEach(function(c) {
    scores[c] = 0;
    if(c === 'EXTR' || !PLAYBOOK[c]) { stats[c] = null; return; }
    var d = PLAYBOOK[c];
    var strengths = d.theirStrengths ? d.theirStrengths.length : 0;
    var attacks = d.theyAttackExtr ? d.theyAttackExtr.length : 0;
    var proofPts = 0;
    var killerQs = 0;
    (d.theirStrengths || []).forEach(function(item) {
      proofPts += (item.proofPoints || []).length;
      killerQs += (item.askThem || []).length;
    });
    (d.theyAttackExtr || []).forEach(function(item) {
      proofPts += (item.proofPoints || []).length;
      killerQs += (item.askThem || []).length;
    });
    var totalClaims = strengths + attacks;
    var counterDepth = totalClaims > 0 ? Math.round((proofPts / totalClaims) * 10) / 10 : 0;
    stats[c] = { strengths: strengths, attacks: attacks, totalClaims: totalClaims, proofPts: proofPts, killerQs: killerQs, counterDepth: counterDepth };
  });

  function getRange(key) {
    var vals = [];
    codes.forEach(function(c) { if(stats[c]) vals.push(stats[c][key]); });
    return { min: Math.min.apply(null, vals), max: Math.max.apply(null, vals) };
  }

  var html = '<div class="tco-table-wrap"><table class="tco-table"><thead><tr>';
  html += '<th style="min-width:140px"></th>';
  codes.forEach(function(c) {
    var v = vendorByCode(c);
    html += '<th style="font-size:11px;text-transform:uppercase;letter-spacing:.5px;text-align:center;color:'+v.color+';'+colBdr+'">'+shortNames[c]+'</th>';
  });
  html += '</tr></thead><tbody>';

  // Row 1: Their Strength Claims (more = harder fight = red)
  var r = getRange('strengths');
  html += '<tr><td style="font-weight:500;font-size:12px;white-space:nowrap;text-align:center">Their Strength Claims</td>';
  codes.forEach(function(c) {
    if(c === 'EXTR') { html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:11px;font-weight:600;color:var(--text-dim)">â€”</span></td>'; return; }
    var val = stats[c].strengths;
    var clr = val <= r.min ? 'var(--green)' : val >= r.max ? 'var(--red)' : 'var(--yellow)';
    html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:13px;font-weight:700;color:'+clr+'">'+val+'</span></td>';
  });
  html += '</tr>';

  // Row 2: Their Attack Vectors (more = harder fight = red)
  r = getRange('attacks');
  html += '<tr><td style="font-weight:500;font-size:12px;white-space:nowrap;text-align:center">Their Attack Vectors</td>';
  codes.forEach(function(c) {
    if(c === 'EXTR') { html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:11px;font-weight:600;color:var(--text-dim)">â€”</span></td>'; return; }
    var val = stats[c].attacks;
    var clr = val <= r.min ? 'var(--green)' : val >= r.max ? 'var(--red)' : 'var(--yellow)';
    html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:13px;font-weight:700;color:'+clr+'">'+val+'</span></td>';
  });
  html += '</tr>';

  // Row 3: Total Threat Claims (separator)
  r = getRange('totalClaims');
  html += '<tr style="border-top:2px solid var(--border)"><td style="font-weight:600;font-size:12px;white-space:nowrap;text-align:center">Total Threat Claims</td>';
  codes.forEach(function(c) {
    if(c === 'EXTR') { html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:11px;font-weight:600;color:var(--text-dim)">â€”</span></td>'; return; }
    var val = stats[c].totalClaims;
    var clr = val <= r.min ? 'var(--green)' : val >= r.max ? 'var(--red)' : 'var(--yellow)';
    html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:13px;font-weight:700;color:'+clr+'">'+val+'</span></td>';
  });
  html += '</tr>';

  // Row 4: Our Proof Points (more = better prepared = green)
  r = getRange('proofPts');
  html += '<tr><td style="font-weight:500;font-size:12px;white-space:nowrap;text-align:center">Our Proof Points</td>';
  codes.forEach(function(c) {
    if(c === 'EXTR') { html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:11px;font-weight:600;color:var(--text-dim)">â€”</span></td>'; return; }
    var val = stats[c].proofPts;
    var clr = val >= r.max ? 'var(--green)' : val <= r.min ? 'var(--red)' : 'var(--yellow)';
    html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:13px;font-weight:700;color:'+clr+'">'+val+'</span></td>';
  });
  html += '</tr>';

  // Row 5: Our Killer Questions (more = better prepared = green)
  r = getRange('killerQs');
  html += '<tr><td style="font-weight:500;font-size:12px;white-space:nowrap;text-align:center">Our Killer Questions</td>';
  codes.forEach(function(c) {
    if(c === 'EXTR') { html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:11px;font-weight:600;color:var(--text-dim)">â€”</span></td>'; return; }
    var val = stats[c].killerQs;
    var clr = val >= r.max ? 'var(--green)' : val <= r.min ? 'var(--red)' : 'var(--yellow)';
    html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:13px;font-weight:700;color:'+clr+'">'+val+'</span></td>';
  });
  html += '</tr>';

  // Row 6: Counter Depth (proof points per claim â€” higher = better)
  r = getRange('counterDepth');
  html += '<tr><td style="font-weight:500;font-size:12px;white-space:nowrap;text-align:center">Counter Depth</td>';
  codes.forEach(function(c) {
    if(c === 'EXTR') { html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:11px;font-weight:600;color:var(--text-dim)">â€”</span></td>'; return; }
    var val = stats[c].counterDepth;
    var clr = val >= r.max ? 'var(--green)' : val <= r.min ? 'var(--red)' : 'var(--yellow)';
    html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:11px;font-weight:600;color:'+clr+'">'+val+'</span><br><span style="font-size:9px;color:var(--text-dim)">pts/claim</span></td>';
  });
  html += '</tr>';

  // Composite: Battle Readiness = proofPts + killerQs*2 + counterDepth*3
  codes.forEach(function(c) {
    if(c === 'EXTR' || !stats[c]) return;
    var s = stats[c];
    scores[c] = s.proofPts + s.killerQs * 2 + Math.round(s.counterDepth * 3);
  });

  html += '<tr style="border-top:2px solid var(--border)">';
  html += '<td style="font-weight:700;font-size:12px;text-align:center">Battle Readiness</td>';
  codes.forEach(function(c) {
    var v = vendorByCode(c);
    if(c === 'EXTR') { html += '<td style="text-align:center;font-weight:700;font-size:12px;color:'+v.color+';'+colBdr+'">â€”</td>'; return; }
    html += '<td style="text-align:center;font-weight:700;font-size:12px;color:'+v.color+';'+colBdr+'">'+scores[c]+'</td>';
  });
  html += '</tr>';

  html += '</tbody></table></div>';
  document.getElementById('pb-h2h').innerHTML = html;
}

