function renderCertMatrix(containerId, data, footnote) {
  var el = document.getElementById(containerId);
  var codes = vendorCodes;
  var shortNames = {EXTR:'EXTREME',CSCO:'CISCO',MRAK:'MERAKI',ARBA:'ARUBA',JNPR:'JUNIPER',FTNT:'FORTINET',RKUS:'RUCKUS',UBNT:'UBIQUITI',ARST:'ARISTA',ALE:'ALCATEL'};
  var labels = {yes:'Yes',no:'No',progress:'In Progress',planned:'Planned',partial:'Partial',na:'N/A'};
  var colBdr = 'border-left:1px dotted rgba(136,136,136,.35)';
  var html = '<div class="section-title">Head-to-Head Comparison</div>';
  html += '<div class="tco-table-wrap" style="margin-bottom:24px"><table class="tco-table"><thead><tr>';
  html += '<th style="text-align:center;font-size:11px;text-transform:uppercase;letter-spacing:.5px">Certification</th>';
  codes.forEach(function(c){ var v=vendorByCode(c); html += '<th style="text-align:center;color:'+v.color+';font-size:11px;text-transform:uppercase;letter-spacing:.5px;'+colBdr+'">'+(shortNames[c]||c)+'</th>'; });
  html += '</tr></thead><tbody>';
  var scores = {};
  codes.forEach(function(c){ scores[c] = 0; });
  data.forEach(function(row){
    html += '<tr><td style="font-weight:500;font-size:12px;white-space:nowrap;text-align:center">'+row.cert+'</td>';
    codes.forEach(function(c){
      var val = row[c] || 'na';
      var clr = val==='yes' ? 'var(--green)' : val==='progress'||val==='planned' ? 'var(--blue)' : val==='partial' ? 'var(--yellow)' : 'var(--red)';
      if(val==='yes') scores[c] += 3;
      else if(val==='progress'||val==='planned') scores[c] += 2;
      else if(val==='partial') scores[c] += 1;
      html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:11px;font-weight:600;color:'+clr+'">'+(labels[val]||val)+'</span></td>';
    });
    html += '</tr>';
  });
  var maxScore = data.length * 3;
  html += '<tr style="border-top:2px solid var(--border)">';
  html += '<td style="font-weight:700;font-size:12px;text-align:center">Composite Score</td>';
  codes.forEach(function(c){
    var v = vendorByCode(c);
    html += '<td style="text-align:center;font-weight:700;font-size:12px;color:'+v.color+';'+colBdr+'">'+scores[c]+'/'+maxScore+'</td>';
  });
  html += '</tr>';
  html += '</tbody></table></div>';
  if(footnote) html += '<div style="margin-top:12px;font-size:12px;color:#3498db">'+footnote+'</div>';
  el.innerHTML = html;
}

