function renderComplianceH2H() {
  var codes = ['EXTR','CSCO','MRAK','ARBA','JNPR','FTNT','RKUS','UBNT','ARST','ALE'];
  var colBdr = 'border-left:1px dotted rgba(136,136,136,.35)';
  var shortNames = {EXTR:'EXTREME',CSCO:'CISCO',MRAK:'MERAKI',ARBA:'ARUBA',JNPR:'JUNIPER',FTNT:'FORTINET',RKUS:'RUCKUS',UBNT:'UBIQUITI',ARST:'ARISTA',ALE:'ALC-LUC'};

  // Build cert lookup from both datasets
  var allCerts = CLOUD_CERTS.concat(PRODUCT_CERTS);
  var certByName = {};
  allCerts.forEach(function(c) { certByName[c.cert] = c; });

  // Map compliance item keywords â†’ cert names
  var CERT_MAP = {
    'NIST 800-53': ['ISO/IEC 27001', 'SOC 2 Type II', 'FedRAMP Moderate'],
    'NIST - CSF': ['ISO/IEC 27001', 'SOC 2 Type II'],
    'DISA': ['FIPS 140-2 Validated', 'FIPS 140-3', 'Common Criteria (NDPP/NDcPP)'],
    'NATO': ['ISO/IEC 27001', 'FIPS 140-2 Validated'],
    'CMMC': ['FIPS 140-2 Validated', 'FIPS 140-3', 'ISO/IEC 27001'],
    'FedRAMP': ['FedRAMP Moderate'],
    'C5': ['C5 (BSI)'],
    'ISMAP': ['ISMAP'],
    'FIPS': ['FIPS 140-2 Validated', 'FIPS 140-3'],
    'Common Criteria': ['Common Criteria (NDPP/NDcPP)'],
    'GovRAMP': ['GovRAMP'],
    'MACsec': ['MACsec (802.1AE)'],
    'FERPA': ['SOC 2 Type II', 'ISO/IEC 27001'],
    'HECVAT': ['SOC 2 Type II', 'ISO/IEC 27001'],
    'NSPM-33': ['ISO/IEC 27001', 'FIPS 140-2 Validated'],
    'CIS Controls': ['ISO/IEC 27001', 'SOC 2 Type II'],
    'ISO 27001': ['ISO/IEC 27001'],
    'ISO-27001': ['ISO/IEC 27001'],
    'SOC2': ['SOC 2 Type II'],
    'Cyber Essentials': ['ISO/IEC 27001'],
    'HIPAA': ['ISO/IEC 27001', 'SOC 2 Type II'],
    'HITECH': ['ISO/IEC 27001', 'SOC 2 Type II'],
    'HITRUST': ['ISO/IEC 27001', 'SOC 2 Type II'],
    'GDPR': ['GDPR Compliance', 'ISO/IEC 27701'],
    'PCI': ['PCI DSS v4.0'],
    'CJIS': ['FIPS 140-2 Validated', 'FIPS 140-3'],
    'EU Cyber': ['ISO/IEC 27001'],
    'SCADA': ['ISO/IEC 27001', 'Common Criteria (NDPP/NDcPP)'],
    'CRA - CE': ['ISO/IEC 27001'],
    'IEC-62443': ['ISO/IEC 27001'],
    'ENERGY STAR': ['ENERGY STAR'],
    'ISO 42001': ['ISO/IEC 42001'],
    'NIST AI': ['ISO/IEC 42001'],
    'EU AI Act': ['ISO/IEC 42001'],
    'ISO 23894': ['ISO/IEC 42001'],
    'OECD': ['ISO/IEC 42001']
  };
  var mapKeys = Object.keys(CERT_MAP);

  function getItemBestStatus(label, vendorCode) {
    var best = 0;
    mapKeys.forEach(function(key) {
      if(label.indexOf(key) === -1) return;
      CERT_MAP[key].forEach(function(certName) {
        var cert = certByName[certName];
        if(!cert) return;
        var s = cert[vendorCode] || 'no';
        if(s === 'yes' && best < 2) best = 2;
        else if((s === 'progress' || s === 'partial' || s === 'planned') && best < 1) best = 1;
      });
    });
    return best;
  }

  function itemHasMapping(label) {
    return mapKeys.some(function(key) { return label.indexOf(key) > -1 && CERT_MAP[key].length > 0; });
  }

  // Compute per-vertical per-vendor scores
  var vertScores = [];
  var grandScores = {};
  codes.forEach(function(c) { grandScores[c] = 0; });

  COMPLIANCE_VERTICALS.forEach(function(vert) {
    var mappable = vert.items.filter(function(it) { return itemHasMapping(it.label); });
    var maxPts = mappable.length * 2;
    var row = { name: vert.name, maxPts: maxPts, scores: {} };
    codes.forEach(function(c) {
      var pts = 0;
      mappable.forEach(function(it) { pts += getItemBestStatus(it.label, c); });
      row.scores[c] = pts;
      grandScores[c] += pts;
    });
    vertScores.push(row);
  });

  var grandMax = 0;
  vertScores.forEach(function(r) { grandMax += r.maxPts; });

  // Build table
  var html = '<div class="tco-table-wrap"><table class="tco-table"><thead><tr>';
  html += '<th style="min-width:140px"></th>';
  codes.forEach(function(c) {
    var v = vendorByCode(c);
    html += '<th style="font-size:11px;text-transform:uppercase;letter-spacing:.5px;text-align:center;color:'+v.color+';'+colBdr+'">'+shortNames[c]+'</th>';
  });
  html += '</tr></thead><tbody>';

  vertScores.forEach(function(row) {
    html += '<tr><td style="font-weight:500;font-size:12px;white-space:nowrap;text-align:center">'+row.name+'</td>';
    if(row.maxPts === 0) {
      codes.forEach(function(c) {
        html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:10px;color:var(--text-dim)">N/A</span></td>';
      });
    } else {
      var vals = [];
      codes.forEach(function(c) { vals.push(row.scores[c]); });
      var vMax = Math.max.apply(null, vals);
      var vMin = Math.min.apply(null, vals);
      codes.forEach(function(c) {
        var pts = row.scores[c];
        var pct = Math.round((pts / row.maxPts) * 100);
        var clr = pts >= vMax ? 'var(--green)' : pts <= vMin ? 'var(--red)' : 'var(--yellow)';
        html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:12px;font-weight:700;color:'+clr+'">'+pct+'%</span><br><span style="font-size:9px;color:var(--text-dim)">'+pts+'/'+row.maxPts+'</span></td>';
      });
    }
    html += '</tr>';
  });

  // Composite
  html += '<tr style="border-top:2px solid var(--border)">';
  html += '<td style="font-weight:700;font-size:12px;text-align:center">Compliance Readiness</td>';
  codes.forEach(function(c) {
    var v = vendorByCode(c);
    html += '<td style="text-align:center;font-weight:700;font-size:12px;color:'+v.color+';'+colBdr+'">'+grandScores[c]+'/'+grandMax+'</td>';
  });
  html += '</tr>';

  html += '</tbody></table></div>';
  document.getElementById('compliance-h2h').innerHTML = html;
}

