/* =========================================================
   TCO CALCULATOR â€” H2H REFERENCE DEPLOYMENT
   ========================================================= */
function renderTcoCalcH2H() {
  var codes = vendorCodes;
  var colBdr = 'border-left:1px dotted rgba(136,136,136,.35)';
  var shortNames = {EXTR:'EXTREME',CSCO:'CISCO',MRAK:'MERAKI',ARBA:'ARUBA',JNPR:'JUNIPER',FTNT:'FORTINET',RKUS:'RUCKUS',UBNT:'UBIQUITI',ARST:'ARISTA',ALE:'ALC-LUC'};

  // Reference deployment: 100 APs, 50 access, 5 core, 10 SD-WAN, $85/hr
  var refAPs = 100, refAccess = 50, refCore = 5, refSDWAN = 10, refLabor = 85;
  var refTotal = refAPs + refAccess + refCore;

  // Compute per-vendor costs
  var data = {};
  codes.forEach(function(c) {
    var hw = TCO_CALC.hw[c];
    var lic = TCO_CALC.lic[c];
    var lab = TCO_CALC.labor[c];
    if(!hw || !lab) { data[c] = null; return; }

    var hwTotal = refAPs * hw.ap + refAccess * hw.access + refCore * hw.core + (hw.sdwan !== null ? refSDWAN * hw.sdwan : 0);
    var mgmt = TCO_CALC.mgmtPlatform[c] || 0;

    var annualLic = 0;
    if(lic === 'fortigate') {
      annualLic = Math.ceil((refTotal + refSDWAN) / 15) * 1500;
    } else {
      annualLic = refAPs * lic.ap + refAccess * lic.access + refCore * lic.core;
      if(lic.sdwan !== null && refSDWAN > 0) annualLic += refSDWAN * lic.sdwan;
    }

    var annualSupport = Math.round(hwTotal * (TCO_CALC.supportPct[c] || 0));
    var totalDevAll = refTotal + refSDWAN;
    var deployHrs = Math.round(lab.deployBase + lab.deployPerDev * totalDevAll);
    var annualOpsHrs = Math.round(lab.opsBase + lab.opsPerDev * totalDevAll);
    var deployLabor = Math.round(deployHrs * refLabor);
    var annualOpsLabor = Math.round(annualOpsHrs * refLabor);
    var capex = hwTotal + mgmt + deployLabor;
    var annualOpex = annualLic + annualSupport + annualOpsLabor;

    data[c] = {
      hwTotal: hwTotal,
      mgmt: mgmt,
      annualLic: annualLic,
      annualSupport: annualSupport,
      deployLabor: deployLabor,
      annualOpsLabor: annualOpsLabor,
      capex: capex,
      annualOpex: annualOpex,
      yr3: capex + annualOpex * 3,
      yr5: capex + annualOpex * 5
    };
  });

  // Row definitions â€” lower cost = greener
  var rows = [
    {label:'Hardware', key:'hwTotal'},
    {label:'Mgmt Platform', key:'mgmt'},
    {label:'Annual Licensing', key:'annualLic'},
    {label:'Annual Support', key:'annualSupport'},
    {label:'Deploy Labor', key:'deployLabor'},
    {label:'Annual Ops Labor', key:'annualOpsLabor'},
    {label:'CAPEX', key:'capex'},
    {label:'Annual OPEX', key:'annualOpex'},
    {label:'3-Year TCO', key:'yr3'},
    {label:'5-Year TCO', key:'yr5'}
  ];

  var html = '<div class="section-title">Head-to-Head: Reference Deployment</div>';
  html += '<p style="font-size:12px;color:var(--text-dim);margin-bottom:16px">Reference: ' + refAPs + ' APs, ' + refAccess + ' access switches, ' + refCore + ' core switches, ' + refSDWAN + ' SD-WAN sites @ $' + refLabor + '/hr. Customize below.</p>';
  html += '<div class="tco-table-wrap" style="margin-bottom:24px"><table class="tco-table"><thead><tr>';
  html += '<th style="text-align:center;font-size:11px;text-transform:uppercase;letter-spacing:.5px">Cost Component</th>';
  codes.forEach(function(c) {
    var v = vendorByCode(c);
    html += '<th style="text-align:center;color:' + v.color + ';font-size:11px;text-transform:uppercase;letter-spacing:.5px;' + colBdr + '">' + (shortNames[c] || v.name.split(' ')[0]) + '</th>';
  });
  html += '</tr></thead><tbody>';

  rows.forEach(function(row) {
    var vals = [];
    codes.forEach(function(c) { vals.push(data[c] ? data[c][row.key] : null); });
    var numVals = vals.filter(function(v) { return v !== null; });
    var minVal = Math.min.apply(null, numVals);
    var maxVal = Math.max.apply(null, numVals);

    html += '<tr' + (row.key === 'yr3' || row.key === 'yr5' ? ' style="border-top:' + (row.key === 'yr3' ? '2' : '1') + 'px solid var(--border)"' : '') + '>';
    var bold = row.key === 'capex' || row.key === 'annualOpex' || row.key === 'yr3' || row.key === 'yr5';
    html += '<td style="font-weight:' + (bold ? '700' : '500') + ';font-size:12px;white-space:nowrap;text-align:center">' + row.label + '</td>';

    codes.forEach(function(c, idx) {
      var val = vals[idx];
      if(val === null) { html += '<td style="text-align:center;' + colBdr + '">â€”</td>'; return; }
      var clr = 'var(--text)';
      if(minVal !== maxVal) {
        // Lower cost = greener
        var pct = maxVal > minVal ? (val - minVal) / (maxVal - minVal) : 0;
        if(pct <= 0.15) clr = 'var(--green)';
        else if(pct >= 0.75) clr = 'var(--red)';
        else clr = 'var(--yellow)';
      }
      var display = '$' + val.toLocaleString();
      html += '<td style="text-align:center;font-weight:' + (bold ? '700' : '600') + ';' + colBdr + '"><span style="font-size:' + (bold ? '12' : '11') + 'px;color:' + clr + '">' + display + '</span></td>';
    });
    html += '</tr>';
  });

  // TCO Score composite: rank by 5-year TCO (lower = better)
  html += '<tr style="border-top:2px solid var(--border)">';
  html += '<td style="font-weight:700;font-size:12px;text-align:center">TCO Rank</td>';
  var ranked = codes.map(function(c) { return {code: c, yr5: data[c] ? data[c].yr5 : Infinity}; });
  ranked.sort(function(a, b) { return a.yr5 - b.yr5; });
  var rankMap = {};
  ranked.forEach(function(r, i) { rankMap[r.code] = i + 1; });

  codes.forEach(function(c) {
    var v = vendorByCode(c);
    if(!data[c]) { html += '<td style="text-align:center;font-weight:700;font-size:12px;' + colBdr + '">â€”</td>'; return; }
    var rank = rankMap[c];
    var clr = rank <= 3 ? 'var(--green)' : rank >= 8 ? 'var(--red)' : 'var(--yellow)';
    html += '<td style="text-align:center;font-weight:700;font-size:12px;color:' + clr + ';' + colBdr + '">#' + rank + '</td>';
  });
  html += '</tr>';
  html += '</tbody></table></div>';

  var el = document.getElementById('tc-h2h');
  if(el) el.innerHTML = html;
}

