function buildAPSummary() {
  var s = {};
  var vendorOrder = ['EXTR','CSCO','MRAK','ARBA','JNPR','FTNT','RKUS','UBNT','ARST','ALE'];
  vendorOrder.forEach(function(v) {
    s[v] = {total:0, wifi7:0, wifi6e:0, wifi6:0, indoor:false, outdoor:false, wallplate:false, rugged:false, minPrice:Infinity, maxPrice:0, flagship:null};
  });
  AP_LINEUP.forEach(function(ap) {
    var v = s[ap.vendor];
    if (!v) return;
    v.total++;
    if (ap.gen === 'Wi-Fi 7') v.wifi7++;
    else if (ap.gen === 'Wi-Fi 6E') v.wifi6e++;
    else v.wifi6++;
    if (ap.env === 'indoor') v.indoor = true;
    if (ap.env === 'outdoor') v.outdoor = true;
    if (ap.segment === 'wallplate') v.wallplate = true;
    if (ap.notes && ap.notes.toLowerCase().indexOf('rugged') >= 0) v.rugged = true;
    if (ap.price < v.minPrice) v.minPrice = ap.price;
    if (ap.price > v.maxPrice) v.maxPrice = ap.price;
    if (ap.flagship) v.flagship = ap;
  });
  return {byVendor: s, vendorOrder: vendorOrder, totalModels: AP_LINEUP.length};
}

