function filterAPH2H() {
  var seg = document.getElementById('ap-h2h-seg').value;
  var vendorOrder = ['EXTR','CSCO','MRAK','ARBA','JNPR','FTNT','RKUS','UBNT','ARST','ALE'];
  var shortNames = {EXTR:'EXTREME',CSCO:'CISCO',MRAK:'MERAKI',ARBA:'ARUBA',JNPR:'JUNIPER',FTNT:'FORTINET',RKUS:'RUCKUS',UBNT:'UBIQUITI',ARST:'ARISTA',ALE:'ALCATEL'};
  var colBdr = 'border-left:1px dotted rgba(136,136,136,.35)';

  var picks = {};
  vendorOrder.forEach(function(code) {
    var candidates = AP_LINEUP.filter(function(ap) { return ap.vendor === code && ap.segment === seg; });
    if (candidates.length === 0) return;
    candidates.sort(function(a, b) {
      var genRank = {'Wi-Fi 7':3,'Wi-Fi 6E':2,'Wi-Fi 6':1};
      var ga = genRank[a.gen] || 0, gb = genRank[b.gen] || 0;
      if (gb !== ga) return gb - ga;
      return b.maxRateNum - a.maxRateNum;
    });
    picks[code] = candidates[0];
  });

  var activeCodes = vendorOrder.filter(function(c){ return picks[c]; });
  var bestRate = 0, lowestPrice = Infinity;
  activeCodes.forEach(function(c) {
    if (picks[c].maxRateNum > bestRate) bestRate = picks[c].maxRateNum;
    if (picks[c].price < lowestPrice) lowestPrice = picks[c].price;
  });

  var specRows = [
    {key:'model', label:'Model'},
    {key:'gen', label:'Wi-Fi Generation'},
    {key:'maxRate', label:'Max Data Rate'},
    {key:'mimo', label:'MIMO Config'},
    {key:'uplink', label:'Uplink'},
    {key:'poe', label:'PoE Requirement'},
    {key:'bands', label:'Band Support'}
  ];

  var h = '<div class="section-title">Head-to-Head Comparison</div>';
  h += '<div class="tco-table-wrap" style="margin-bottom:24px"><table class="tco-table"><thead><tr>';
  h += '<th style="text-align:center;font-size:11px;text-transform:uppercase;letter-spacing:.5px">Spec</th>';
  activeCodes.forEach(function(c){ var v=vendorByCode(c); h += '<th style="text-align:center;color:'+v.color+';font-size:11px;text-transform:uppercase;letter-spacing:.5px;'+colBdr+'">'+(shortNames[c]||c)+'</th>'; });
  h += '</tr></thead><tbody>';

  specRows.forEach(function(row){
    h += '<tr><td style="font-weight:500;font-size:12px;white-space:nowrap;text-align:center">'+row.label+'</td>';
    activeCodes.forEach(function(c){
      var ap = picks[c];
      var val = ap[row.key] || 'â€”';
      var style = 'text-align:center;'+colBdr;
      if(row.key === 'maxRate' && ap.maxRateNum === bestRate) style += ';color:var(--green);font-weight:700';
      else if(row.key === 'gen') { var clr = val === 'Wi-Fi 7' ? 'var(--green)' : val === 'Wi-Fi 6E' ? 'var(--yellow)' : 'var(--red)'; style += ';color:'+clr+';font-weight:600'; }
      h += '<td style="'+style+'"><span style="font-size:11px;font-weight:600">'+val+'</span></td>';
    });
    h += '</tr>';
  });

  // Price row
  h += '<tr style="border-top:2px solid var(--border)"><td style="font-weight:700;font-size:12px;text-align:center">Price</td>';
  activeCodes.forEach(function(c){
    var ap = picks[c];
    var clr = ap.price === lowestPrice ? 'var(--green)' : ap.price <= lowestPrice * 1.5 ? 'var(--yellow)' : 'var(--red)';
    h += '<td style="text-align:center;font-weight:700;font-size:12px;color:'+clr+';'+colBdr+'">'+ap.priceStr+'</td>';
  });
  h += '</tr>';

  h += '</tbody></table></div>';
  document.getElementById('ap-h2h-body').innerHTML = h;
}

