  var shortNames = {EXTR:'EXTREME',CSCO:'CISCO',MRAK:'MERAKI',ARBA:'ARUBA',JNPR:'JUNIPER',FTNT:'FORTINET',RKUS:'RUCKUS',UBNT:'UBIQUITI',ARST:'ARISTA',ALE:'ALC-LUC'};

  // Aggregate per vendor from PLAYBOOK
  var stats = {};
  codes.forEach(function(c) {
    var pb = PLAYBOOK[c];
    if(!pb || c === 'EXTR') {
      stats[c] = {strengths:0, attacks:0, proofPts:0, killerQs:0, counterDepth:0, pricingCats:0};
      return;
    }
    var totalCounterLen = 0, claimCount = 0;
    var proofSet = [], killerSet = [];
    pb.theirStrengths.forEach(function(s) {
      claimCount++;
      totalCounterLen += (s.counter||'').length;
      if(s.proofPoints) s.proofPoints.forEach(function(p) { if(proofSet.indexOf(p)===-1) proofSet.push(p); });
      if(s.killerQuestion) killerSet.push(s.killerQuestion);
    });
    pb.theyAttackExtr.forEach(function(a) {
      claimCount++;
      totalCounterLen += (a.counter||'').length;
      if(a.proofPoints) a.proofPoints.forEach(function(p) { if(proofSet.indexOf(p)===-1) proofSet.push(p); });
      if(a.killerQuestion) killerSet.push(a.killerQuestion);
    });
    // Count pricing categories with data for this vendor
    var pricingCats = 0;
    [WIRELESS_PRICING, SWITCHING_PRICING, SDWAN_PRICING, MGMT_PRICING].forEach(function(arr) {
      if(typeof arr !== 'undefined') {
        for(var i=0;i<arr.length;i++) { if(arr[i].vendor===c) { pricingCats++; break; } }
      }
    });
    stats[c] = {
      strengths: pb.theirStrengths.length,
      attacks: pb.theyAttackExtr.length,
      proofPts: proofSet.length,
      killerQs: killerSet.length,
      counterDepth: claimCount > 0 ? Math.round(totalCounterLen / claimCount) : 0,
      pricingCats: pricingCats
    };
  });

  var rows = [
    {label:'Their Strength Claims', key:'strengths', higher:'green'},
    {label:'Their Attack Vectors', key:'attacks', higher:'green'},
    {label:'Our Proof Points', key:'proofPts', higher:'green'},
    {label:'Killer Questions', key:'killerQs', higher:'green'},
    {label:'Counter Depth (avg chars)', key:'counterDepth', higher:'green'},
    {label:'Pricing Categories', key:'pricingCats', higher:'green'}
  ];

  var html = '<div class="section-title">Head-to-Head: Battle Card Readiness</div>';
  html += '<p style="font-size:12px;color:var(--text-dim);margin-bottom:16px">How well-prepared we are to compete against each vendor. More claims analyzed = deeper competitive intelligence.</p>';
  html += '<div class="tco-table-wrap" style="margin-bottom:24px"><table class="tco-table"><thead><tr>';
