function initLowFruit() {
  var dropZone = document.getElementById('lf-drop-zone');
  var fileInput = document.getElementById('lf-file-input');
  if(!dropZone || !fileInput) return;

  dropZone.addEventListener('dragover', function(e) {
    e.preventDefault(); e.stopPropagation();
    dropZone.classList.add('dragover');
  });
  dropZone.addEventListener('dragleave', function(e) {
    e.preventDefault(); e.stopPropagation();
    dropZone.classList.remove('dragover');
  });
  dropZone.addEventListener('drop', function(e) {
    e.preventDefault(); e.stopPropagation();
    dropZone.classList.remove('dragover');
    if(e.dataTransfer.files.length > 0) analyzeLFFile(e.dataTransfer.files[0]);
  });
  fileInput.addEventListener('change', function() {
    if(fileInput.files.length > 0) analyzeLFFile(fileInput.files[0]);
  });
  document.addEventListener('paste', handleLFPaste);
}

function handleLFPaste(e) {
  var activeView = document.querySelector('.view.active');
  if(!activeView || activeView.id !== 'view-lowfruit') return;
  var items = (e.clipboardData || e.originalEvent.clipboardData).items;
  for(var i = 0; i < items.length; i++) {
    if(items[i].type.indexOf('image') !== -1) {
      e.preventDefault();
      var file = items[i].getAsFile();
      analyzeLFFile(file);
      return;
    }
  }
}

function analyzeLFFile(file) {
  var loading = document.getElementById('lf-loading');
  var results = document.getElementById('lf-results');
  var preview = document.getElementById('lf-preview');
  var previewImg = document.getElementById('lf-preview-img');
  results.innerHTML = '';
  loading.classList.add('active');

  if(file.type.startsWith('image/')) {
    preview.classList.add('active');
    var reader2 = new FileReader();
    reader2.onload = function(ev) { previewImg.src = ev.target.result; };
    reader2.readAsDataURL(file);
  } else {
    preview.classList.remove('active');
  }

  var formData = new FormData();
  formData.append('file', file);

  fetch('/api/analyze', { method:'POST', body:formData })
    .then(function(r) { return r.json(); })
    .then(function(data) {
      loading.classList.remove('active');
      if(data.error) { results.innerHTML = '<div class="lf-no-results"><p>Error: ' + data.error + '</p></div>'; return; }
      processLFResults(data.devices || []);
    })
    .catch(function(err) {
      loading.classList.remove('active');
      results.innerHTML = '<div class="lf-no-results"><p>Request failed: ' + err.message + '</p></div>';
    });
}

function analyzeLFText() {
  var text = document.getElementById('lf-text-input').value.trim();
  if(!text) return;
  var loading = document.getElementById('lf-loading');
  var results = document.getElementById('lf-results');
  var preview = document.getElementById('lf-preview');
  preview.classList.remove('active');
  results.innerHTML = '';
  loading.classList.add('active');

  fetch('/api/analyze', {
    method:'POST',
    headers:{'Content-Type':'application/json'},
    body:JSON.stringify({text:text})
  })
    .then(function(r) { return r.json(); })
    .then(function(data) {
      loading.classList.remove('active');
      if(data.error) { results.innerHTML = '<div class="lf-no-results"><p>Error: ' + data.error + '</p></div>'; return; }
      processLFResults(data.devices || []);
    })
    .catch(function(err) {
      loading.classList.remove('active');
      results.innerHTML = '<div class="lf-no-results"><p>Request failed: ' + err.message + '</p></div>';
    });
}

function processLFResults(devices) {
  var results = [];
  for(var i = 0; i < devices.length; i++) {
    var dev = devices[i];
    var match = null;
    var modelStr = (dev.model || '').trim();
    for(var j = 0; j < EOS_EOL_DB.length; j++) {
      var entry = EOS_EOL_DB[j];
      try {
        var re = new RegExp(entry.pattern, 'i');
        if(re.test(modelStr)) { match = entry; break; }
      } catch(e) {}
    }
    var qty = parseInt(dev.quantity) || 1;
    results.push({
      model: modelStr,
      quantity: qty,
      vendor: dev.vendor || '',
      notes: dev.notes || '',
      match: match,
      totalUpgrade: match ? match.estUpgradePrice * qty : 0
    });
  }
  renderLFResults(results);
}

function renderLFResults(results) {
  var container = document.getElementById('lf-results');
  if(!results.length) {
    container.innerHTML = '<div class="lf-no-results"><svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><circle cx="12" cy="12" r="10"/><path d="M16 16s-1.5-2-4-2-4 2-4 2"/><line x1="9" y1="9" x2="9.01" y2="9"/><line x1="15" y1="9" x2="15.01" y2="9"/></svg><p>No devices found in the input. Try a different file or format.</p></div>';
    return;
  }

  var totalDevices = 0, eosEolCount = 0, totalRevenue = 0;
  for(var i = 0; i < results.length; i++) {
    totalDevices += results[i].quantity;
    if(results[i].match && (results[i].match.status === 'eol' || results[i].match.status === 'eos')) {
      eosEolCount += results[i].quantity;
    }
    totalRevenue += results[i].totalUpgrade;
  }
  var matchedCount = 0;
  for(var k = 0; k < results.length; k++) { if(results[k].match) matchedCount++; }

  var html = '<div class="lf-summary-bar">';
  html += '<div class="lf-summary-card"><div class="lf-sc-label">Total Devices</div><div class="lf-sc-value blue">' + totalDevices + '</div></div>';
  html += '<div class="lf-summary-card"><div class="lf-sc-label">EOS/EOL Devices</div><div class="lf-sc-value red">' + eosEolCount + '</div></div>';
  html += '<div class="lf-summary-card"><div class="lf-sc-label">Models Matched</div><div class="lf-sc-value yellow">' + matchedCount + ' / ' + results.length + '</div></div>';
  html += '<div class="lf-summary-card"><div class="lf-sc-label">Est. Upgrade Revenue</div><div class="lf-sc-value green">$' + totalRevenue.toLocaleString() + '</div></div>';
  html += '</div>';

  for(var r = 0; r < results.length; r++) {
    var item = results[r];
    var m = item.match;
    var statusClass = m ? m.status : 'unknown';
    var statusLabel = m ? (m.status === 'eol' ? 'End of Life' : m.status === 'eos' ? 'End of Sale' : m.status === 'lastcall' ? 'Last Call' : 'Active') : 'Unknown';
    var urgencyClass = m ? m.urgency : '';
    var urgencyLabel = m ? (m.urgency.charAt(0).toUpperCase() + m.urgency.slice(1)) : '';

    html += '<div class="lf-device-card">';
    html += '<div class="lf-device-header">';
    html += '<div><span class="lf-device-model">' + item.model + '</span> <span class="lf-device-qty">x' + item.quantity + '</span>';
    if(item.vendor) html += ' <span class="lf-device-qty">(' + item.vendor + ')</span>';
    html += '</div>';
    html += '<div><span class="lf-status-badge ' + statusClass + '">' + statusLabel + '</span>';
    if(urgencyClass) html += '<span class="lf-urgency ' + urgencyClass + '">' + urgencyLabel + '</span>';
    html += '</div></div>';

    if(m) {
      html += '<dl class="lf-device-details">';
      html += '<dt>Category</dt><dd>' + m.category + '</dd>';
      html += '<dt>Generation</dt><dd>' + m.generation + '</dd>';
      html += '<dt>End of Sale</dt><dd>' + m.eosDate + '</dd>';
      html += '<dt>End of Life</dt><dd>' + m.eolDate + '</dd>';
      if(m.notes) { html += '<dt>Notes</dt><dd>' + m.notes + '</dd>'; }
      html += '</dl>';
      html += '<div class="lf-replacement-box">';
      html += '<div class="lf-rep-title">Recommended Replacement</div>';
      html += '<div class="lf-rep-model">' + m.replacementModel + '</div>';
      if(m.estUpgradePrice > 0) html += '<div class="lf-rep-price">Est. $' + m.estUpgradePrice.toLocaleString() + ' per unit &mdash; $' + item.totalUpgrade.toLocaleString() + ' total (' + item.quantity + ' units)</div>';
      else html += '<div class="lf-rep-price">Cloud migration &mdash; subscription-based (eliminates hardware cost)</div>';
      html += '<div class="lf-rep-notes">' + m.replacement + '</div>';
      html += '</div>';
    } else {
      html += '<p style="font-size:13px;color:var(--text-dim);">No EOS/EOL match found in Extreme Networks database. This may be a current-generation product or a third-party device.</p>';
    }
    html += '</div>';
  }
  container.innerHTML = html;
}

/* =========================================================
   LOW HANGING FRUIT â€” H2H DISPLACEMENT OPPORTUNITY
   ========================================================= */
function renderLowFruitH2H() {
  var codes = vendorCodes;
  var colBdr = 'border-left:1px dotted rgba(136,136,136,.35)';
  var shortNames = {EXTR:'EXTREME',CSCO:'CISCO',MRAK:'MERAKI',ARBA:'ARUBA',JNPR:'JUNIPER',FTNT:'FORTINET',RKUS:'RUCKUS',UBNT:'UBIQUITI',ARST:'ARISTA',ALE:'ALC-LUC'};

  // Count EOS/EOL entries per vendor from EOSEOL_MASTER
  var stats = {};
  codes.forEach(function(c){ stats[c] = {total:0, eol:0, eos:0, lastcall:0, critical:0, high:0}; });
  if(typeof EOSEOL_MASTER !== 'undefined') {
    EOSEOL_MASTER.forEach(function(e){
      var vc = e.vendorCode;
      if(!stats[vc]) return;
      stats[vc].total++;
      if(e.status === 'eol') stats[vc].eol++;
      else if(e.status === 'eos') stats[vc].eos++;
      else if(e.status === 'lastcall') stats[vc].lastcall++;
      if(e.urgency === 'critical') stats[vc].critical++;
      else if(e.urgency === 'high') stats[vc].high++;
    });
  }

  // Count Extreme replacements per source vendor from REPLACEMENTS
  var extrReplacements = {};
  codes.forEach(function(c){ extrReplacements[c] = 0; });
  if(typeof REPLACEMENTS !== 'undefined') {
    REPLACEMENTS.forEach(function(r){
      if(r.target === 'EXTR' && extrReplacements.hasOwnProperty(r.source)) {
        extrReplacements[r.source]++;
      }
    });
  }

  // Count EOS_EOL_DB entries for Extreme internal refresh
  var extrInternalRefresh = {total:0, eol:0, eos:0};
  if(typeof EOS_EOL_DB !== 'undefined') {
    EOS_EOL_DB.forEach(function(e){
      extrInternalRefresh.total++;
      if(e.status === 'eol') extrInternalRefresh.eol++;
      else if(e.status === 'eos') extrInternalRefresh.eos++;
    });
  }

  // Rows definition
  var rows = [
    {label:'Total Legacy Products', key:'total', higher:'green'},
    {label:'End of Life (Critical)', key:'eol', higher:'green'},
    {label:'End of Sale', key:'eos', higher:'green'},
    {label:'High Urgency', key:'high_urgency', higher:'green'},
    {label:'Extreme Replacements', key:'replacements', higher:'green'},
    {label:'Last Call / Active Risk', key:'lastcall', higher:'green'}
  ];

  var html = '<div class="section-title">Head-to-Head: Displacement Opportunity</div>';
  html += '<div class="tco-table-wrap" style="margin-bottom:24px"><table class="tco-table"><thead><tr>';
  html += '<th style="text-align:center;font-size:11px;text-transform:uppercase;letter-spacing:.5px">Metric</th>';
  codes.forEach(function(c){
    var v = vendorByCode(c);
    html += '<th style="text-align:center;color:'+v.color+';font-size:11px;text-transform:uppercase;letter-spacing:.5px;'+colBdr+'">'+(shortNames[c]||v.name.split(' ')[0])+'</th>';
  });
  html += '</tr></thead><tbody>';

  rows.forEach(function(row){
    var vals = [];
    codes.forEach(function(c){
      var val = 0;
      if(c === 'EXTR') {
        // For Extreme, show internal refresh stats
        if(row.key === 'total') val = extrInternalRefresh.total;
        else if(row.key === 'eol') val = extrInternalRefresh.eol;
        else if(row.key === 'eos') val = extrInternalRefresh.eos;
        else if(row.key === 'high_urgency') val = 0;
        else if(row.key === 'replacements') val = 'â€”';
        else if(row.key === 'lastcall') val = 0;
      } else {
        if(row.key === 'total') val = stats[c].total;
        else if(row.key === 'eol') val = stats[c].eol;
        else if(row.key === 'eos') val = stats[c].eos;
        else if(row.key === 'high_urgency') val = stats[c].high;
        else if(row.key === 'replacements') val = extrReplacements[c];
        else if(row.key === 'lastcall') val = stats[c].lastcall;
      }
      vals.push({code:c, val:val});
    });

    // Rank for color coding (higher = more opportunity = greener, for competitors)
    var numericVals = vals.filter(function(v){ return typeof v.val === 'number' && v.code !== 'EXTR'; }).map(function(v){ return v.val; });
    var maxVal = Math.max.apply(null, numericVals.concat([0]));
    var minVal = Math.min.apply(null, numericVals.concat([0]));

    html += '<tr>';
    html += '<td style="font-weight:500;font-size:12px;white-space:nowrap;text-align:center">'+row.label+'</td>';
    vals.forEach(function(v){
      var display = v.val;
      var clr = 'var(--text-dim)';
      if(v.code === 'EXTR') {
        if(row.key === 'replacements') {
          display = 'â€”';
          clr = 'var(--text-dim)';
        } else {
          clr = '#7b2ff7';
        }
      } else if(typeof v.val === 'number' && maxVal > minVal) {
        var pct = (v.val - minVal) / (maxVal - minVal);
        if(pct >= 0.7) clr = 'var(--green)';
        else if(pct >= 0.3) clr = 'var(--yellow)';
        else clr = 'var(--text-dim)';
      } else if(typeof v.val === 'number' && v.val > 0) {
        clr = 'var(--green)';
      }
      html += '<td style="text-align:center;'+colBdr+'"><span style="font-size:12px;font-weight:600;color:'+clr+'">'+display+'</span></td>';
    });
    html += '</tr>';
  });

  // Composite: Displacement Score = total*1 + eol*3 + eos*2 + high*1 + replacements*2 + lastcall*1
  html += '<tr style="border-top:2px solid var(--border)">';
  html += '<td style="font-weight:700;font-size:12px;text-align:center">Displacement Score</td>';
  var compScores = [];
  codes.forEach(function(c){
    var s;
    if(c === 'EXTR') {
      s = null;
    } else {
      s = stats[c].total + stats[c].eol * 3 + stats[c].eos * 2 + stats[c].high + extrReplacements[c] * 2 + stats[c].lastcall;
    }
    compScores.push({code:c, score:s});
  });
  var numericScores = compScores.filter(function(s){ return s.score !== null; }).map(function(s){ return s.score; });
  var maxComp = Math.max.apply(null, numericScores.concat([0]));
  var minComp = Math.min.apply(null, numericScores.concat([0]));

  compScores.forEach(function(s){
    var v = vendorByCode(s.code);
    if(s.score === null) {
      html += '<td style="text-align:center;font-weight:700;font-size:12px;color:var(--text-dim);'+colBdr+'">â€”</td>';
    } else {
      var clr = v.color;
      if(maxComp > minComp) {
        var pct = (s.score - minComp) / (maxComp - minComp);
        if(pct >= 0.7) clr = 'var(--green)';
        else if(pct >= 0.3) clr = 'var(--yellow)';
        else clr = 'var(--text-dim)';
      }
      html += '<td style="text-align:center;font-weight:700;font-size:12px;color:'+clr+';'+colBdr+'">'+s.score+'</td>';
    }
  });
  html += '</tr>';
  html += '</tbody></table></div>';

  var el = document.getElementById('lf-h2h');
  if(el) el.innerHTML = html;
}

