var erateUploadedFile = null;
var erateRfpFiles = [];

function initErate470() {
  var dropZone = document.getElementById('erate-drop-zone');
  var fileInput = document.getElementById('erate-file-input');
  if(!dropZone || !fileInput) return;

  dropZone.addEventListener('dragover', function(e) {
    e.preventDefault(); e.stopPropagation();
    dropZone.classList.add('dragover');
  });
  dropZone.addEventListener('dragleave', function(e) {
    e.preventDefault(); e.stopPropagation();
    dropZone.classList.remove('dragover');
  });
  dropZone.addEventListener('drop', function(e) {
    e.preventDefault(); e.stopPropagation();
    dropZone.classList.remove('dragover');
    if(e.dataTransfer.files.length > 0) handleErateFile(e.dataTransfer.files[0]);
  });
  fileInput.addEventListener('change', function() {
    if(fileInput.files.length > 0) handleErateFile(fileInput.files[0]);
  });

  // RFP drop zone
  var rfpZone = document.getElementById('erate-rfp-drop-zone');
  var rfpInput = document.getElementById('erate-rfp-file-input');
  if(!rfpZone || !rfpInput) return;

  rfpZone.addEventListener('dragover', function(e) {
    e.preventDefault(); e.stopPropagation();
    rfpZone.classList.add('dragover');
  });
  rfpZone.addEventListener('dragleave', function(e) {
    e.preventDefault(); e.stopPropagation();
    rfpZone.classList.remove('dragover');
  });
  rfpZone.addEventListener('drop', function(e) {
    e.preventDefault(); e.stopPropagation();
    rfpZone.classList.remove('dragover');
    for(var i = 0; i < e.dataTransfer.files.length; i++) {
      handleErateRfpFile(e.dataTransfer.files[i]);
    }
  });
  rfpInput.addEventListener('change', function() {
    for(var i = 0; i < rfpInput.files.length; i++) {
      handleErateRfpFile(rfpInput.files[i]);
    }
    rfpInput.value = '';
  });
}

function handleErateFile(file) {
  erateUploadedFile = file;
  var dropZone = document.getElementById('erate-drop-zone');
  dropZone.innerHTML = '<svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" style="width:32px;height:32px;color:var(--green);margin-bottom:8px"><path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"/><polyline points="22 4 12 14.01 9 11.01"/></svg>' +
    '<p><strong>' + file.name + '</strong> (' + (file.size/1024).toFixed(1) + ' KB)</p>' +
    '<p style="font-size:12px;color:var(--text-dim)">File ready â€” fill in form details below and click Generate</p>';
}

function handleErateRfpFile(file) {
  if(erateRfpFiles.length >= 10) { alert('Maximum 10 RFP files allowed.'); return; }
  for(var i = 0; i < erateRfpFiles.length; i++) {
    if(erateRfpFiles[i].name === file.name && erateRfpFiles[i].size === file.size) return;
  }
  erateRfpFiles.push(file);
  renderErateRfpFileList();
}

function removeErateRfpFile(idx) {
  erateRfpFiles.splice(idx, 1);
  renderErateRfpFileList();
}

function renderErateRfpFileList() {
  var list = document.getElementById('erate-rfp-file-list');
  if(!list) return;
  var html = '';
  for(var i = 0; i < erateRfpFiles.length; i++) {
    var f = erateRfpFiles[i];
    var sizeStr = f.size > 1048576 ? (f.size/1048576).toFixed(1) + ' MB' : (f.size/1024).toFixed(1) + ' KB';
    html += '<div class="erate-rfp-file-item">';
    html += '<span class="erate-rfp-file-name">' + f.name + '</span>';
    html += '<span class="erate-rfp-file-size">' + sizeStr + '</span>';
    html += '<button class="erate-rfp-remove" onclick="removeErateRfpFile(' + i + ')" title="Remove">&times;</button>';
    html += '</div>';
  }
  list.innerHTML = html;
  var tabBtn = document.getElementById('erate-rfp-tab-btn');
  if(tabBtn) tabBtn.style.display = erateRfpFiles.length > 0 ? '' : 'none';
}

function generateErate470() {
  var loading = document.getElementById('erate-loading');
  var output = document.getElementById('erate-output');
  var loadingText = loading ? loading.querySelector('p') : null;
  var text = document.getElementById('erate-text-input').value.trim();

  if(!erateUploadedFile && !text) {
    alert('Please upload a Form 470 file or paste the text content.');
    return;
  }

  var formData = {
    applicant: document.getElementById('erate-applicant').value.trim() || 'School District',
    fundingYear: document.getElementById('erate-funding-year').value.trim() || '2026',
    sites: parseInt(document.getElementById('erate-sites').value) || 10,
    discount: parseInt(document.getElementById('erate-discount').value) || 80,
    partner: document.getElementById('erate-partner').value.trim() || 'Authorized E-Rate Partner'
  };

  output.classList.remove('active');
  loading.classList.add('active');

  var hasRfp = erateRfpFiles.length > 0;
  if(loadingText) {
    loadingText.innerHTML = 'Analyzing Form 470 and generating response package' + (hasRfp ? ' + ' + erateRfpFiles.length + ' RFP document(s)' : '') + '&hellip;';
  }

  // Always use FormData when we have RFP files or a 470 file
  var body;
  var fetchOpts;
  if(erateUploadedFile || hasRfp) {
    body = new FormData();
    if(erateUploadedFile) body.append('file', erateUploadedFile);
    if(text && !erateUploadedFile) body.append('text', text);
    body.append('formData', JSON.stringify(formData));
    for(var i = 0; i < erateRfpFiles.length; i++) {
      body.append('rfpFiles', erateRfpFiles[i]);
    }
    fetchOpts = { method:'POST', body:body };
  } else {
    fetchOpts = {
      method:'POST',
      headers:{'Content-Type':'application/json'},
      body:JSON.stringify({ text:text, formData:formData })
    };
  }

  fetch('/api/erate470', fetchOpts)
    .then(function(r) { return r.json(); })
    .then(function(data) {
      loading.classList.remove('active');
      if(data.error) {
        output.classList.add('active');
        document.getElementById('erate470-cover').innerHTML = '<div style="padding:20px;color:var(--red)">Error: ' + data.error + '</div>';
        return;
      }
      if(data.applicantName) {
        formData.applicant = data.applicantName;
        document.getElementById('erate-applicant').value = data.applicantName;
      }
      // Show/hide RFP tab based on response data
      var rfpTabBtn = document.getElementById('erate-rfp-tab-btn');
      if(rfpTabBtn) rfpTabBtn.style.display = data.rfpResponse ? '' : 'none';
      renderErate470Package(data, formData);
    })
    .catch(function(err) {
      loading.classList.remove('active');
      output.classList.add('active');
      document.getElementById('erate470-cover').innerHTML = '<div style="padding:20px;color:var(--red)">Request failed: ' + err.message + '</div>';
    });
}

