function erateCreatePdf(title, buildFn) {
  var jsPDF = window.jspdf.jsPDF;
  var doc = new jsPDF({unit:'mm', format:'letter'});
  var pageW = doc.internal.pageSize.getWidth();
  var margin = 18;
  var usable = pageW - margin * 2;
  var y = 20;

  function checkPage(need) { if(y + need > 260) { doc.addPage(); y = 20; } }
  function heading(text) { checkPage(14); doc.setFontSize(16); doc.setFont('helvetica','bold'); doc.setTextColor(93,47,186); doc.text(text, margin, y); y += 10; }
  function subheading(text) { checkPage(12); doc.setFontSize(12); doc.setFont('helvetica','bold'); doc.setTextColor(50,50,50); doc.text(text, margin, y); y += 8; }
  function bodyText(text) { doc.setFontSize(10); doc.setFont('helvetica','normal'); doc.setTextColor(60,60,60); var lines = doc.splitTextToSize(text, usable); checkPage(lines.length * 5 + 2); doc.text(lines, margin, y); y += lines.length * 5 + 4; }
  function smallText(text,indent) { doc.setFontSize(9); doc.setFont('helvetica','normal'); doc.setTextColor(80,80,80); var lines = doc.splitTextToSize(text, usable - (indent||0)); checkPage(lines.length * 4 + 2); doc.text(lines, margin + (indent||0), y); y += lines.length * 4 + 2; }
  function spacer(h) { y += (h||4); }
  function hrLine() { checkPage(4); doc.setDrawColor(180); doc.line(margin, y, pageW - margin, y); y += 4; }

  // Header bar
  doc.setFillColor(93,47,186);
  doc.rect(0, 0, pageW, 14, 'F');
  doc.setFontSize(9); doc.setFont('helvetica','bold'); doc.setTextColor(255,255,255);
  doc.text('EXTREME NETWORKS  |  E-Rate 470 Response Package', margin, 9);
  y = 22;

  heading(title);
  hrLine();

  buildFn(doc, {checkPage:checkPage, heading:heading, subheading:subheading, bodyText:bodyText, smallText:smallText, spacer:spacer, hrLine:hrLine, margin:margin, usable:usable, getY:function(){return y;}, setY:function(v){y=v;}, pageW:pageW});

  // Footer on each page
  var totalPages = doc.internal.getNumberOfPages();
  for(var p = 1; p <= totalPages; p++) {
    doc.setPage(p);
    doc.setFontSize(8); doc.setFont('helvetica','normal'); doc.setTextColor(150);
    doc.text('Extreme Networks Confidential  |  Page ' + p + ' of ' + totalPages, margin, 272);
  }
  return doc.output('arraybuffer');
}

